import "./App.css";
import io from "socket.io-client";
import { useState } from "react";
import Chat from "./Chat";
import bg from "./pictures/newbg.jpeg";

const socket = io.connect("http://localhost:3001");

function App() {
  const [username, setUsername] = useState("");
  const [room, setRoom] = useState("");
  const [gender, setGender] = useState(""); 

  const [showChat, setShowChat] = useState(false);

  const joinRoom = () => {
    if (username !== "" && room !== "" && gender !== "") { 
      socket.emit("join_room", room);
      setShowChat(true);
    }
  };

  return (
    <div className="App" style={{
      backgroundImage: `url(${bg})`,
      backgroundSize: '70%', // Cover the entire container
      backgroundRepeat: 'no-repeat', 
      borderRadius:'20px'// Don't repeat the background image
    }}>
      {!showChat ? (
        <div className="joinChatContainer">
          <h3>Join A Chat</h3>
          <input
            type="text"
            placeholder="John..."
            onChange={(event) => {
              setUsername(event.target.value);
            }}
          />
          <div className="genders">
            <input type="radio" name="gender" value="male" onChange={(event) => setGender(event.target.value)} /> Male<br />
            <input type="radio" name="gender" value="female" onChange={(event) => setGender(event.target.value)} /> Female<br />
            <input type="radio" name="gender" value="other" onChange={(event) => setGender(event.target.value)} /> Other<br />
          </div>
          <div>
            <input type="date" id="birthday"/>
          </div>
          <input
            type="text"
            placeholder="Room ID..."
            onChange={(event) => {
              setRoom(event.target.value);
            }}
          />
          <button onClick={joinRoom}>Join A Room</button>
        </div>
      ) : (
        <Chat socket={socket} username={username} room={room} />
      )}
      <h3 className="secnd"> Connect with Multiples users from around the world <br/><br/>anonymously and Have fun !</h3>
    </div>
  );
}

export default App;
